function newtrim($str)
{
    $j=strlen($str);
    $result="";

    for($i=0; $i<$j; $i++) {
        switch($str[$i]) {
            case chr(8):
                # È¥µôÍË¸ñ
            case chr(9):
                # È¥µôtab
            case chr(10):
                # È¥µô»»ĞĞ
            case chr(13):
                # È¥µô»Ø³µ
            case chr(255):
                #È¥µôÌØÊâ¿Õ¸ñ
                break;
            default:
                $result .= $str[$i];
        }
    }
    return $result;
}


