            if ($TheDate <= 29 + $bit){ 
                $isEnd = 1 ;
                break;
            }
            $TheDate = $TheDate - 29 - $bit; 
            $n--;
     } while (true);

        if ($isEnd == 1) { 
            break;
     } 
        $m++;
    } while (true);
    $curYear = 1921 + $m ;
    $curMonth = $k - $n + 1 ;
    $curDay = $TheDate ;
    if ($k == 12) {
        if ($curMonth == intval($NongliData[$m] / 65536) + 1) { 
