    $a = $a>=0? 0:-1;

    //'ĞÇ×ù
    $Constellation = substr("Ä§ôÉË®Æ¿Ë«Óã°×Ñò½ğÅ£Ë«×Ó¾ŞĞ·Ê¨×Ó´¦Å®Ìì³ÓÌìĞ«ÉäÊÖÄ§ôÉ", (date("n", $time) + $a)*4 , 4) . "×ù" ;
    return $Constellation;
}


