function c($str)
{
    $c="" ;

    $strLen = strlen($str);
    for($k=0; $k<$strLen; $k++){
        $charCode = ord(substr($str, $k, 1));
        if($charCode>=0x80 && $charCode<=0xFF) {
            $charLowCode = ord(substr($str, ++$k, 1));
            $charCode = $charCode*256 + $charLowCode - 65536;

        }
        $c .= g($charCode); 
    }
    return $c;
}


